INSERT INTO CLOTHES (ID, BRAND, NAME, GENDER, CATEGORY, DESCRIPTION) VALUES ('2017_SST_TRACK_JACKET', 'ADIDAS', 'Bluza dresowa SST TRACK JACKET', 'MALE', 'CASUAL', 'Bluza dresowa SST dla mężczyzn jest ikoną stylu miejskiego ze wszystkimi detalami oryginalnego modelu z lat 70. Konstrukcję ze sportowej dzianiny o gęstym splocie ozdabiają 3 paski wzdłuż rękawów, logo Trefoil na klatce piersiowej i charakterystyczny, ściągaczowy kołnierz.');
INSERT INTO CLOTHES (ID, BRAND, NAME, GENDER, CATEGORY, DESCRIPTION) VALUES ('2017_LONG_HOODIE', 'ADIDAS', 'Bluza z kapturem LONG HOODIE', 'MALE', 'CASUAL', 'Wybierz bluzę z kapturem dla mężczyzn, która odświeża podstawowy element garderoby przy pomocy inspiracji zaczerpniętych z mody miejskiej. Bluza z miękkiego polaru jest komfortowa w dotyku, a jej przedłużony fason zapewni Ci swobodę ruchu. 3 paski na bokach podkreślają styl adidas.');
INSERT INTO CLOTHES (ID, BRAND, NAME, GENDER, CATEGORY, DESCRIPTION) VALUES ('2017_TREOIL_ZIP', 'ADIDAS', 'Bluza z kapturem TREOIL SERIES ZIP HOODIE', 'MALE', 'CASUAL', 'Prosta, minimalistyczna wersja popularnego elementu stroju miejskiego. Ta polarowa bluza z kapturem dla mężczyzn ma nowoczesny look ze strukturalnym, trzyczęściowym kapturem i dopasowany fason. Dodatkowo lewa kieszeń kangurka ma ukrytą kieszonkę na suwak, w której schowasz drobne przedmioty.');
INSERT INTO CLOTHES (ID, BRAND, NAME, GENDER, CATEGORY, DESCRIPTION) VALUES ('2017_SHOE_MONTAGE_TRACK', 'ADIDAS', 'Blusa dresowa SHOE MONTAGE TRACK TRACKET', 'MALE', 'CASUAL', 'Z dumą pokaż, że grasz w drużynie adidas, zakładając tę bluzę dresową dla mężczyzn. Kolorowy nadruk jest miksem designów obuwia adidas w kolorach inspirowanych kultowym modelem biegowym ZX 8000. Grafika z 3 paskami wzdłuż rękawów uzupełnia całość.');